mapper.select:
select collection_menu.user_id as user_id,username,collection_menu.menu_id as menu_id,menu_name,whether
from collection_menu,user,menu

    user.user_id=collection_menu.user_id and menu.menu_id=collection_menu.menu_id

        and collection_menu.user_id=#{userId}


        and user.username like "%"#{username}"%"



        limit #{start},#{rows}

select username,menu_name,whether
from collection_menu,menu,user
where user.user_id=collection_menu.user_id and
menu.menu_id=collection_menu.menu_id and
collection_menu.user_id=#{userId}


    limit #{start},#{rows}
