mapper.select:
select username,name,content,time
from nutrition_record,user
where nutrition_record.user_id = user.user_id
    and user.user_id = #{userId}


    limit #{start},#{rows}

mapper.update:
update nutrition_record
set content=#{content}
where user_id=#{user_id} and name=#{name}
    and time=#{time}
