mapper.select:
select nutrition_record.user_id as user_id,username,nutrition_record.name as name,content,time
from nutrition_record,user

    user.user_id=nutrition_record.user_id

        and nutrition_record.user_id=#{userId}


        and user.username like "%"#{username}"%"



        limit #{start},#{rows}

select user.user_id,username,name,content,time
from nutrition_record,user
where nutrition_record.user_id = user.user_id
and user.user_id = #{userId}


    limit #{start},#{rows}

select *
from nutrition_record
where user_id=#{user_id} and time=#{time}

mapper.update:
update nutrition_record
set content=content+#{content}
where user_id=#{user_id} and name=#{name}
  and time=#{time}
