mapper.update:
update recommend_menu
set whether=#{whether}
where user_id=#{user_id} and menu_id=#{menu_id} and time=#{time}

mapper.select:
select user.user_id, username,menu.menu_id,menu_name,time,whether
from recommend_menu,user,menu
where user.user_id=recommend_menu.user_id and
      menu.menu_id=recommend_menu.menu_id and
      user.user_id=#{userId}


    limit #{start},#{rows}
