mapper.select:
select recommend_menu.user_id as user_id,username,recommend_menu.menu_id as menu_id,menu_name,time,whether
from recommend_menu,user,menu

    user.user_id=recommend_menu.user_id and menu.menu_id=recommend_menu.menu_id

        and recommend_menu.user_id=#{userId}


        and user.username like "%"#{username}"%"



        limit #{start},#{rows}

select user_id, menu_id
from recommend_menu
where user_id=#{userId} and time=#{time}

mapper.update:
update recommend_menu
set whether=#{whether}
where user_id=#{user_id} and menu_id=#{menu_id} and time like #{time}

mapper.select:
select user.user_id, username,menu.menu_id,menu_name,time,whether
from recommend_menu,user,menu
where user.user_id=recommend_menu.user_id and
menu.menu_id=recommend_menu.menu_id and
user.user_id=#{userId}


    limit #{start},#{rows}
